footer {
  width: 100%;
  color: $main-bg-color;
  background-color: $main-text-color;
  padding-top: 70px;
  padding-bottom: 50px;

  & .section-container-wrapper {
    text-align: center;
  }

  .nav-list {
    justify-content: center;
    margin-bottom: 60px;
  }
  
  & a {
    color: $main-bg-color;
  }

  & h2 {
    color: $accent-color;
    text-transform: uppercase;
    margin-bottom: 50px;
  }
}

.footer-subtitle {
  margin-bottom: 30px;
  margin-bottom: 50px;
}

.copyright {
  margin-bottom: 60px;
}

.social-links {
  justify-content: center;

  & li:not(:last-child) {
    margin-right: 90px;
  }
  & svg {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 60px;
    height: 36px;
    fill: $main-bg-color;
  }
}

sources.sass\\main.scss:
@import "variables";
@import "utils";
@import "sections/sections";

html {
  scroll-behavior: smooth;
}

body {
  overflow-x: hidden;
  padding-top: 90px;
  background-color: $main-bg-color;
  font-family: "Work Sans", sans-serif;
  color: $main-text-color;
}

a {
  color: $main-text-color;
  text-decoration: none;
  font-size: 26px;
  font-weight: bold;
  transition: color 250ms linear;
  @media screen and(min-width: $breakpoint-desktop) {
    font-size: 18px;
  }

  &:hover,
  &:focus {
    color: $accent-color;
  }
}

h1 {
  font-size: 74px;
  font-weight: bold;
  margin-bottom: 45px;
}

h2 {
  font-size: 48.425px;
  font-weight: bold;
}

ul {
  display: flex;
  list-style: none;
}

section {
  width: 100%;
  padding: 20px 0;
}

select {
  border: none;
  color: $main-text-color;
  text-decoration: none;
  font-size: 18px;
  font-weight: bold;
}

button {
  border: none;
  font-size: 18px;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 250ms linear, color 250ms linear;

  &:hover,
  &:focus {
    background-color: $accent-color;
  }
}

.section-content-wrapper {
  width: 80%;
  padding: 0 5px;
  margin: 0 auto;
  @media screen and (min-width: $breakpoint-desktop) {
    max-width: 1200px;
  }
}

.hover-button {
  width: 200px;
  height: 60px;
  color: $main-text-color;
  background-color: transparent;
  border-radius: 30px;
  box-shadow: 0px 5px 10px 0px rgba(255, 57, 70, 0.15);
  &:hover,
  &:focus {
    background-color: $accent-color;
    color: $main-bg-color;
  }
}
