@import "variables";
@import "utils";
@import "sections/sections";

html {
  scroll-behavior: smooth;
}

body {
  overflow-x: hidden;
  padding-top: 90px;
  background-color: $main-bg-color;
  font-family: "Work Sans", sans-serif;
  color: $main-text-color;
}

a {
  color: $main-text-color;
  text-decoration: none;
  font-size: 26px;
  font-weight: bold;
  @media screen and(min-width: $breakpoint-desktop) {
    font-size: 18px;
  }
}

h1 {
  font-size: 74px;
  font-weight: bold;
  margin-bottom: 45px;
}

h2 {
  font-size: 48.425px;
  font-weight: bold;
}

ul {
  display: flex;
  list-style: none;
}

section {
  width: 100%;
  padding: 20px 0;
}

select {
  border: none;
  color: $main-text-color;
  text-decoration: none;
  font-size: 18px;
  font-weight: bold;
}

button {
  border: none;
  font-size: 18px;
  font-weight: bold;
  cursor: pointer;
}
.section-content-wrapper {
  width: 80%;
  padding: 0 5px;
  margin: 0 auto;
  @media screen and (min-width: $breakpoint-desktop) {
    max-width: 1200px;
  }
}

.hover-button {
  width: 200px;
  height: 60px;
  color: $main-text-color;
  background-color: transparent;
  border-radius: 30px;
  box-shadow: 0px 5px 10px 0px rgba(255, 57, 70, 0.15);
  &:hover,
  &:focus {
    background-color: $accent-color;
    color: $main-bg-color;
  }
}
